import React, { useState, useCallback } from "react";
import Cropper from "react-easy-crop";
import "bootstrap/dist/css/bootstrap.min.css";
import { FaUpload } from "react-icons/fa";
import tshirtImage from "../assets/tshirt.png";
import hoodieImage from "../assets/hoodies.png";
import mugImage from "../assets/mugs.png";
import { FaUndo, FaRedo } from "react-icons/fa";

const Process = () => {
  const [selectedProduct, setSelectedProduct] = useState(tshirtImage);
  const [uploadedImage, setUploadedImage] = useState(null);
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);

  const handleProductChange = (product) => {
    setSelectedProduct(product);
    setUploadedImage(null);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setUploadedImage(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const onCropComplete = useCallback((croppedArea, croppedAreaPixels) => {
    setCroppedAreaPixels(croppedAreaPixels);
  }, []);

  return (
    <div className="container py-4 bg-light">
      <div className="row">
        {/* Left Panel */}
        <div className="col-3 p-3 rounded">
          <h5>Select Product</h5>
          <button className="btn btn-light bg-light mb-2" onClick={() => handleProductChange(tshirtImage)}>
            <img src={tshirtImage} alt="T-Shirt" className="w-100 p-5" />
            T-Shirt
          </button>
          <button className="btn btn-light bg-light mb-2" onClick={() => handleProductChange(hoodieImage)}>
            <img src={hoodieImage} alt="Hoodie" className="w-100 p-5" />
            Hoodie
          </button>
          <button className="btn bg-light btn-light" onClick={() => handleProductChange(mugImage)}>
            <img src={mugImage} alt="Mug" className="w-100 p-5" />
            Mug
          </button>
        </div>

        {/* Middle Panel: Product Preview & Upload */}
        <div className="col-6 p-4 d-flex justify-content-center rounded border border-0 border-primary  bg-white">
        <div className="d-flex flex-column justify-content-center align-items-center rounded border border-0 border-primary m-4 bg-light  "  style={{
      width: "800px", height: "800px"}}>
  <div className="position-relative d-flex justify-content-center align-items-center" style={{ width: "400px", height: "450px" }}>
    <img src={selectedProduct} alt="Product" className="w-100 h-100 p-5 bg-light" />
    <label
      className="position-absolute d-flex flex-column align-items-center justify-content-center"
      style={{
        top: "50%",
        left: "50%",
        width: "200px",
        height: "200px",
        transform: "translate(-50%, -50%)",
        border: "2px dashed grey",
        cursor: "pointer",
      }}
    >



      <FaUpload size={40} className="text-white" />
      <span className="text-secondary fw-bold text-center">Drop your design <br />here<br/>or click to upload</span>
      <input type="file" accept="image/*" onChange={handleImageUpload} hidden />
    </label>
    {uploadedImage && (
      <div
        className="position-absolute d-flex justify-content-center align-items-center"
        style={{
          top: "50%",
          left: "50%",
          width: "200px",
          height: "200px",
          transform: "translate(-50%, -50%)",
        }}
      >
        <Cropper
          image={uploadedImage}
          crop={crop}
          zoom={zoom}
          aspect={1} // Square cropping
          onCropChange={setCrop}
          onZoomChange={setZoom}
          onCropComplete={onCropComplete}
        />
      </div>
    )}
  </div>

</div>
 <div className=" mb-3">
    <button className="btn btn-light d-flex align-items-center">
      <FaUndo className="me-2" /> Undo
    </button>
    <button className="btn btn-light d-flex align-items-center">
      <FaRedo className="me-2" /> Redo
    </button>
  </div>


        </div>

        {/* Right Panel: Customize Options */}
        <div className="col-3 p-3">
          <h5>Customize Options</h5>
          <label>Size</label>
          <select className="form-control mb-2">
            <option>Select size</option>
            <option>S</option>
            <option>M</option>
            <option>L</option>
            <option>XL</option>
          </select>
          <label>Color</label>
          <div className="d-flex mb-2">
            {["white", "black", "red", "blue", "green", "yellow"].map((color) => (
              <div key={color} className="rounded-circle border me-2" style={{ width: "30px", height: "30px", backgroundColor: color, cursor: "pointer" }}></div>
            ))}
          </div>
          <label>Quantity</label>
          <div className="d-flex">
            <button className="btn btn-outline-secondary">-</button>
            <input type="number" className="form-control text-center mx-2" defaultValue={1} style={{ width: "50px" }} />
            <button className="btn btn-outline-secondary">+</button>
          </div>
          <button className="btn btn-warning w-100 mt-3">Add to Cart</button>
        </div>
      </div>
    </div>
  );
};

export default Process;
