import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Style/Cart.css"; // Custom styles
import tshirt from "../assets/tshirts.png";

const Cart = () => {
  return (
    <div className="container bg-light py-4">
      <div className="bg-white rounded p-4 shadow">
        <div className="col-12">
          <p className="text-dark review fw-bold">Review Your Order</p>
          <hr />

          <div className="row align-items-center">
            {/* Product Image */}
            <div className="col-md-2">
              <img src={tshirt} alt="Product" className="img-fluid rounded" />

            </div>
            <div className="col-md-2">
              <p>Basic T-Shirt</p>
            </div>

            {/* Product Details */}
            <div className="col-md-6">
              <h5 className="mb-1">T-Shirt</h5>
              <p className="text-muted">Size: M | Color: Black</p>
              <p className="fw-bold">$19.99</p>
            </div>

            {/* Actions */}
            <div className="col-md-4 text-end">
              <button className="btn btn-danger me-2">Remove</button>
              <button className="btn btn-primary">Checkout</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
