"use client"

import { useState, useRef } from "react"
import Cropper from "react-easy-crop"
import "bootstrap/dist/css/bootstrap.min.css"
import { FaUpload, FaUndo, FaRedo } from "react-icons/fa"

const Process = () => {
  // State for selected product
  const [selectedProduct, setSelectedProduct] = useState("tshirt")

  // State for customization options
  const [size, setSize] = useState("")
  const [color, setColor] = useState("white")
  const [quantity, setQuantity] = useState(1)

  // State for image cropping
  const [image, setImage] = useState(null)
  const [crop, setCrop] = useState({ x: 0, y: 0 })
  const [zoom, setZoom] = useState(1)

  // File input ref
  const fileInputRef = useRef(null)

  // Handle product selection
  const handleProductSelect = (product) => {
    setSelectedProduct(product)
  }

  // Handle file upload
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      const reader = new FileReader()
      reader.addEventListener("load", () => {
        setImage(reader.result)
      })
      reader.readAsDataURL(e.target.files[0])
    }
  }

  // Handle crop complete
  const onCropComplete = (croppedArea, croppedAreaPixels) => {
    // This is just UI, so we're not doing anything with the crop result
    console.log(croppedAreaPixels)
  }

  // Handle quantity change
  const handleQuantityChange = (value) => {
    const newQuantity = Math.max(1, value)
    setQuantity(newQuantity)
  }

  // Product images
  const productImages = {
    tshirt: "/placeholder.svg?height=200&width=200",
    hoodie: "/placeholder.svg?height=200&width=200",
    mug: "/placeholder.svg?height=200&width=200",
  }

  // Product preview images
  const previewImages = {
    tshirt: "/placeholder.svg?height=400&width=400",
    hoodie: "/placeholder.svg?height=400&width=400",
    mug: "/placeholder.svg?height=400&width=400",
  }

  return (
    <div className="container-fluid py-4">
      <div className="row">
        {/* Product Selection */}
        <div className="col-md-3">
          <h4 className="mb-4">Select Product</h4>

          <div
            className="card mb-3 cursor-pointer"
            onClick={() => handleProductSelect("tshirt")}
            style={{ border: selectedProduct === "tshirt" ? "2px solid #007bff" : "" }}
          >
            <div className="card-body text-center">
              <img
                src={productImages.tshirt || "/placeholder.svg"}
                alt="T-Shirt"
                className="img-fluid mb-2"
                style={{ maxHeight: "120px" }}
              />
              <h5>T-Shirt</h5>
            </div>
          </div>

          <div
            className="card mb-3 cursor-pointer"
            onClick={() => handleProductSelect("hoodie")}
            style={{ border: selectedProduct === "hoodie" ? "2px solid #007bff" : "" }}
          >
            <div className="card-body text-center">
              <img
                src={productImages.hoodie || "/placeholder.svg"}
                alt="Hoodie"
                className="img-fluid mb-2"
                style={{ maxHeight: "120px" }}
              />
              <h5>Hoodie</h5>
            </div>
          </div>

          <div
            className="card mb-3 cursor-pointer"
            onClick={() => handleProductSelect("mug")}
            style={{ border: selectedProduct === "mug" ? "2px solid #007bff" : "" }}
          >
            <div className="card-body text-center">
              <img
                src={productImages.mug || "/placeholder.svg"}
                alt="Mug"
                className="img-fluid mb-2"
                style={{ maxHeight: "120px" }}
              />
              <h5>Mug</h5>
            </div>
          </div>
        </div>

        {/* Product Preview & Design Area */}
        <div className="col-md-6">
          <div className="card h-100">
            <div
              className="card-body d-flex flex-column justify-content-center align-items-center position-relative"
              style={{ minHeight: "500px" }}
            >
              {image ? (
                <div style={{ position: "relative", width: "100%", height: "400px" }}>
                  <Cropper
                    image={image}
                    crop={crop}
                    zoom={zoom}
                    aspect={1}
                    onCropChange={setCrop}
                    onCropComplete={onCropComplete}
                    onZoomChange={setZoom}
                  />
                </div>
              ) : (
                <div
                  className="d-flex flex-column justify-content-center align-items-center border border-dashed p-5"
                  style={{
                    width: "80%",
                    height: "400px",
                    cursor: "pointer",
                    backgroundImage: `url(${previewImages[selectedProduct]})`,
                    backgroundSize: "contain",
                    backgroundPosition: "center",
                    backgroundRepeat: "no-repeat",
                  }}
                  onClick={() => fileInputRef.current.click()}
                >
                  <div className="text-center p-4 bg-light bg-opacity-75 rounded">
                    <FaUpload className="mb-2" size={24} />
                    <p className="mb-0">Drop your design here</p>
                    <p className="mb-0">or click to upload</p>
                  </div>
                </div>
              )}

              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                accept="image/*"
                style={{ display: "none" }}
              />

              <div className="d-flex mt-3 gap-2">
                <button className="btn btn-light">
                  <FaUndo /> Undo
                </button>
                <button className="btn btn-light">
                  <FaRedo /> Redo
                </button>
              </div>

              <button className="btn btn-primary mt-3" onClick={() => fileInputRef.current.click()}>
                Upload Design
              </button>
            </div>
          </div>
        </div>

        {/* Customization Options */}
        <div className="col-md-3">
          <h4 className="mb-4">Customize Options</h4>

          <div className="mb-3">
            <label className="form-label">Size</label>
            <select className="form-select" value={size} onChange={(e) => setSize(e.target.value)}>
              <option value="">Select size</option>
              <option value="s">Small</option>
              <option value="m">Medium</option>
              <option value="l">Large</option>
              <option value="xl">X-Large</option>
              <option value="xxl">XX-Large</option>
            </select>
          </div>

          <div className="mb-3">
            <label className="form-label">Color</label>
            <div className="d-flex gap-2">
              {["white", "black", "red", "blue", "green", "gold"].map((colorOption) => (
                <div
                  key={colorOption}
                  className="rounded-circle"
                  style={{
                    width: "30px",
                    height: "30px",
                    backgroundColor: colorOption,
                    border: color === colorOption ? "2px solid #007bff" : "1px solid #dee2e6",
                    cursor: "pointer",
                  }}
                  onClick={() => setColor(colorOption)}
                />
              ))}
            </div>
          </div>

          <div className="mb-4">
            <label className="form-label">Quantity</label>
            <div className="input-group">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={() => handleQuantityChange(quantity - 1)}
              >
                -
              </button>
              <input
                type="number"
                className="form-control text-center"
                value={quantity}
                onChange={(e) => handleQuantityChange(Number.parseInt(e.target.value) || 1)}
                min="1"
              />
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={() => handleQuantityChange(quantity + 1)}
              >
                +
              </button>
            </div>
          </div>

