import React, { useState, useCallback } from "react";
import Cropper from "react-easy-crop";
import "bootstrap/dist/css/bootstrap.min.css";
import { FaUpload, FaUndo, FaRedo } from "react-icons/fa";
import tshirtImage from "../assets/tshirt.png";
import hoodieImage from "../assets/hoodies.png";
import mugImage from "../assets/mugs.png";

const Process = () => {
  const [selectedProduct, setSelectedProduct] = useState(tshirtImage);
  const [uploadedImage, setUploadedImage] = useState(null);
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
  const [history, setHistory] = useState([]);
  const [redoStack, setRedoStack] = useState([]);

  const handleProductChange = (product) => {
    setSelectedProduct(product);
    setUploadedImage(null);
    setHistory([]);
    setRedoStack([]);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setUploadedImage(reader.result);
      reader.readAsDataURL(file);
      setHistory([]);
      setRedoStack([]);
    }
  };

  const onCropComplete = useCallback((croppedArea, croppedAreaPixels) => {
    setCroppedAreaPixels(croppedAreaPixels);
    setHistory((prev) => [...prev, { crop, zoom }]);
    setRedoStack([]);
  }, [crop, zoom]);

  const handleUndo = () => {
    if (history.length > 1) {
      setRedoStack((prev) => [history[history.length - 1], ...prev]);
      const previousState = history[history.length - 2];
      setCrop(previousState.crop);
      setZoom(previousState.zoom);
      setHistory(history.slice(0, -1));
    }
  };

  const handleRedo = () => {
    if (redoStack.length > 0) {
      const nextState = redoStack[0];
      setCrop(nextState.crop);
      setZoom(nextState.zoom);
      setHistory((prev) => [...prev, nextState]);
      setRedoStack(redoStack.slice(1));
    }
  };

  return (
    <div className="container py-4 bg-light">
      <div className="row">
        <div className="col-3 p-3 rounded">
          <h5>Select Product</h5>
          <button className="btn btn-light mb-2" onClick={() => handleProductChange(tshirtImage)}>
            <img src={tshirtImage} alt="T-Shirt" className="w-100 p-5" />
            T-Shirt
          </button>
          <button className="btn btn-light mb-2" onClick={() => handleProductChange(hoodieImage)}>
            <img src={hoodieImage} alt="Hoodie" className="w-100 p-5" />
            Hoodie
          </button>
          <button className="btn btn-light" onClick={() => handleProductChange(mugImage)}>
            <img src={mugImage} alt="Mug" className="w-100 p-5" />
            Mug
          </button>
        </div>

        <div className="col-6 p-4 d-flex justify-content-center rounded border bg-white">
          <div className="d-flex flex-column justify-content-center align-items-center border bg-light" style={{ width: "800px", height: "800px" }}>
            <div className="position-relative d-flex justify-content-center align-items-center" style={{ width: "400px", height: "450px" }}>
              <img src={selectedProduct} alt="Product" className="w-100 h-100 p-5 bg-light" />
              <label className="position-absolute d-flex flex-column align-items-center justify-content-center" style={{ top: "50%", left: "50%", width: "200px", height: "200px", transform: "translate(-50%, -50%)", border: "2px dashed grey", cursor: "pointer" }}>
                <FaUpload size={40} className="text-white" />
                <span className="text-secondary fw-bold text-center">Drop your design <br /> here <br /> or click to upload</span>
                <input type="file" accept="image/*" onChange={handleImageUpload} hidden />
              </label>
              {uploadedImage && (
                <div className="position-absolute d-flex justify-content-center align-items-center" style={{ top: "50%", left: "50%", width: "200px", height: "200px", transform: "translate(-50%, -50%)" }}>
                  <Cropper image={uploadedImage} crop={crop} zoom={zoom} aspect={1} onCropChange={setCrop} onZoomChange={setZoom} onCropComplete={onCropComplete} />
                </div>
              )}
            </div>
            <div className="mt-3 d-flex gap-3">
              <button className="btn btn-secondary" onClick={handleUndo} disabled={history.length <= 1}><FaUndo /> Undo</button>
              <button className="btn btn-secondary" onClick={handleRedo} disabled={redoStack.length === 0}><FaRedo /> Redo</button>
            </div>
          </div>
        </div>

        <div className="col-3 p-3">
          <h5>Customize Options</h5>
          <label>Size</label>
          <select className="form-control mb-2">
            <option>Select size</option>
            <option>S</option>
            <option>M</option>
            <option>L</option>
            <option>XL</option>
          </select>
          <label>Color</label>
          <div className="d-flex mb-2">
            {["white", "black", "red", "blue", "green", "yellow"].map((color) => (
              <div key={color} className="rounded-circle border me-2" style={{ width: "30px", height: "30px", backgroundColor: color, cursor: "pointer" }}></div>
            ))}
          </div>
          <label>Quantity</label>
          <div className="d-flex">
            <button className="btn btn-outline-secondary">-</button>
            <input type="number" className="form-control text-center mx-2" defaultValue={1} style={{ width: "50px" }} />
            <button className="btn btn-outline-secondary">+</button>
          </div>
          <button className="btn btn-warning w-100 mt-3">Add to Cart</button>
        </div>
      </div>
    </div>
  );
};

export default Process;
