import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Style/Cart.css"; // Custom styles
import { FaTrash } from "react-icons/fa"; // Import delete icon
import tshirt from "../assets/tshirts.png";

const Cart = () => {
  const [cartItem, setCartItem] = useState({
    name: "Basic T-Shirt",
    color: "White",
    size: "M",
    price: 29.99,
    quantity: 1,
  });

  const handleDelete = () => {
    setCartItem(null); // Removes item from cart
  };

  return (
    <div className="container bg-light py-4">
      <div className="bg-white rounded p-4 shadow">
        <div className="col-12">
          <p className="text-dark review fw-bold">Review Your Order</p>
          <hr />

          {cartItem ? (
            <div className="row align-items-center">
              {/* Product Image */}
              <div className="col-4 d-flex">
                <img src={tshirt} alt="Product" className="img-fluid rounded" />
                <div className="px-4">
                  <p className="text-dark">{cartItem.name}</p>
                  <p className="text-dark">
                    {cartItem.color} â€¢ Size: {cartItem.size}
                  </p>
                  <p className="text-dark">
                    - {cartItem.quantity} +
                  </p>
                </div>
              </div>

              {/* Price and Delete */}
              <div className="col-8 d-flex flex-column align-items-end">
                <p className="text-dark fw-bold">${cartItem.price.toFixed(2)}</p>
                <button className="btn btn-danger" onClick={handleDelete}>
                  <FaTrash /> Delete
                </button>
                <p className="text-dark fw-bold">${(cartItem.price * cartItem.quantity).toFixed(2)}</p>
              </div>
            </div>
          ) : (
            <p className="text-center text-muted">Your cart is empty.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Cart;
